CREATE TABLE `d0270edf`.`wcp_config` ( `id` INT(8) UNSIGNED NOT NULL AUTO_INCREMENT , `alias` VARCHAR(255) NOT NULL , `value` VARCHAR(255) NOT NULL , PRIMARY KEY (`id`)) ENGINE = MyISAM;

CREATE TABLE `d0270edf`.`cms_user` ( `userID` INT(8) UNSIGNED NOT NULL AUTO_INCREMENT , `name` VARCHAR(150) NOT NULL , `password` VARCHAR(100) NOT NULL , `mail` VARCHAR(150) NOT NULL , `birthday` DATE NOT NULL , `gender` INT(2) NOT NULL , `origin` VARCHAR(155) NOT NULL , `signatur` MEDIUMTEXT NOT NULL , `points` INT(10) NOT NULL , `rank` VARCHAR(18) NOT NULL , `language` VARCHAR(2) NOT NULL , `last_on` VARCHAR(34) NOT NULL , `userIP` VARCHAR(34) NOT NULL , `created` VARCHAR(34) NOT NULL , `rec_pw` VARCHAR(100) NOT NULL , `rec_hash` VARCHAR(32) NOT NULL , `rec_created` VARCHAR(32) NOT NULL , PRIMARY KEY (`userID`)) ENGINE = MyISAM;

CREATE TABLE `d0270edf`.`cms_projects` ( `id` INT(8) UNSIGNED NOT NULL AUTO_INCREMENT , `topic` VARCHAR(255) NOT NULL , `url` VARCHAR(255) NOT NULL , `content` LONGTEXT NOT NULL , `version` VARCHAR(100) NOT NULL , `active` INT(1) NOT NULL , `userID` INT(8) NOT NULL , `userIP` VARCHAR(34) NOT NULL , `created` VARCHAR(34) NOT NULL , `edit` VARCHAR(34) NOT NULL , PRIMARY KEY (`proID`)) ENGINE = MyISAM;

CREATE TABLE `d0270edf`.`cms_interests` ( `id` INT(8) UNSIGNED NOT NULL AUTO_INCREMENT , `content` LONGTEXT NOT NULL , `active` INT(1) NOT NULL , `userID` INT(8) NOT NULL , `userIP` VARCHAR(34) NOT NULL , `created` VARCHAR(34) NOT NULL , `edit` VARCHAR(34) NOT NULL , PRIMARY KEY (`intID`)) ENGINE = MyISAM;

CREATE TABLE `d0270edf`.`cms_skills_icons` ( `id` INT(8) UNSIGNED NOT NULL AUTO_INCREMENT , `icon` VARCHAR(255) NOT NULL , `active` INT(1) NOT NULL , `order_by` INT(1) NOT NULL , PRIMARY KEY (`id`)) ENGINE = MyISAM;

CREATE TABLE `d0270edf`.`cms_skills_working` ( `id` INT(8) UNSIGNED NOT NULL AUTO_INCREMENT , `working` VARCHAR(255) NOT NULL , `active` INT(1) NOT NULL , `order_by` INT(1) NOT NULL , PRIMARY KEY (`id`)) ENGINE = MyISAM;

CREATE TABLE `d0270edf`.`cms_about_me` ( `id` INT(8) UNSIGNED NOT NULL AUTO_INCREMENT , `firstname` VARCHAR(100) NOT NULL , `lastname` VARCHAR(100) NOT NULL , `street` VARCHAR(150) NOT NULL , `nr` VARCHAR(8) NOT NULL , `code` INT(8) NOT NULL , `city` VARCHAR(150) NOT NULL , `country` INT(8) NOT NULL , `mail` VARCHAR(150) NOT NULL , `content` LONGTEXT NOT NULL , `ip` VARCHAR(34) NOT NULL , `created` VARCHAR(34) NOT NULL , `edit` VARCHAR(34) NOT NULL , PRIMARY KEY (`id`)) ENGINE = MyISAM;